package lab;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 float a=2.50f , b=5.50f;
 System.out.println("BEFORE SWAP");
 System.out.println("a is"+a);
 System.out.println("b is"+b);
 float c=a;
 a=b;
 b=c;
 System.out.println("AFTER SWAP");
 System.out.println("a is"+a);
 System.out.println("b is"+b);
	}

}
